import React,{useState} from 'react'

export default function TextForm(props) {
    const handleUpperclick=()=>{
       console.log("upperCase was clicked");
       var textnew=text.toUpperCase();
       setText(textnew);
       document.getElementById('uclk').innerText="convert to Lower text"
    }
    
    const handleOnChange=(event)=>{
        console.log("on change")
        setText(event.target.value)

    }
    const[text,setText]=useState("your text here");

  return (
<div className="container my-4 mt-4">
<div className="mb-3">
  <label htmlFor="exampleFormControlTextarea1" className="form-label">{props.heading}</label>
  <textarea className="form-control" id="exampleFormControlTextarea1" rows="3" value={text}  onChange={handleOnChange}></textarea>
</div>
<button className="btn btn-primary" id='uclk' onClick={handleUpperclick}>Convert to uppercase</button>
</div>
  )
}
