import React,{useState} from 'react'

export default function TextForm(props) {
    const handleUpperclick=()=>{
       var textnew=text.toUpperCase();
       setText(textnew);
    };
    const handleLowerclick=()=>{
      var textnew=text.toLowerCase();
      setText(textnew);
   };

   const handleClear=()=>{
    setText("");
   };
   
   const handleTrim=()=>{
    var textnew = text.trim();
    setText(textnew);
   };

//  const handleTime=()=>{
//       setInterval(()=>{
//       },1000);
//     };
    
    const handleOnChange=(event)=>{
        console.log("on change")
        setText(event.target.value)

    }
    
    const[text,setText]=useState(" ");

  return (
    <>
<div className="container my-4 mt-4">
<div className="mb-3">
  <label htmlFor="exampleFormControlTextarea1" className="form-label">{props.heading}</label>
  <textarea className="form-control" id="exampleFormControlTextarea1" rows="3" value={text}  onChange={handleOnChange}></textarea>
</div>
<button className="btn btn-primary mx-2" onClick={handleUpperclick}>Convert to uppercase</button>
<button className="btn btn-secondary mx-2" onClick={handleLowerclick}>Convert to lowercase</button>
<button className="btn btn-danger" onClick={handleClear}>Clear</button>
<button className="btn btn-success mx-2" onClick={handleTrim}>Remove Space</button>


</div>
<div className='container'>
  <h1>{text.length} characters</h1>
  <h1>{text.split(" ").length} words</h1>
  <h2>text summary</h2>{text}

</div>

</>
  )
}
